var res = {};
var arr = [];

var str = msg.payload;
arr = str.split("\n");
str = arr[0];
arr = str.split("\r");
res.payload = {};
switch(arr[0])
{
    case "Number of attempts exceeded":
        res.topic = "permissionDenied";
        res.payload = {"data": arr[0], "hubId": 1}
        break;
    case "Motion detected!":
        res.topic = "suspiciousMotion";
        res.payload = {"data": arr[0], "hubId": 1}
        break;
    case "Door open more than 30s":
        res.topic = "doorOpen";
        res.payload = {"data": arr[0], "hubId": 1}
        break;
}

return res;

var res = {};

res.payload = new Date(msg.payload);
res.topic = msg.topic;

return res;

context.data = context.data || {};

switch(msg.topic)
{
    case "permissionDenied":
        context.data.permissionDenied = msg.payload;
        msg = null;
        break;
    case "suspiciousMotion":
        context.data.suspiciousMotion = msg.payload;
        msg = null;
        break;
    case "doorOpen":
        context.data.doorOpen = msg.payload;
        msg = null;
        break;
    case "time":
        context.data.time = msg.payload;
        msg = null;
        break;
    default:
        msg = null;
        break;
}

if(context.data.permissionDenied != null && context.data.time != null)
{
    res = {};
    res.payload = JSON.stringify(context.data);
    res.topic = "medium/house1/permissionDenied";
    context.data = null;
    return res;
}

else if(context.data.suspiciousMotion != null && context.data.time != null)
{
    res = {};
    res.payload = JSON.stringify(context.data);
    res.topic = "high/house1/suspiciousMotion";
    context.data = null;
    return res;
}

else if(context.data.doorOpen != null && context.data.time != null)
{
    res = {};
    res.payload = JSON.stringify(context.data);
    res.topic = "low/house1/doorOpen";
    context.data = null;
    return res;
}
